        "code":"let p0x = +document.getElementById(\"p0x\").value;\nlet p0y = +document.getElementById(\"p0y\").value;\nlet c0x = +document.getElementById(\"c0x\").value;\nlet c0y = +document.getElementById(\"c0y\").value;\nlet c1x = +document.getElementById(\"c1x\").value;\nlet c1y = +document.getElementById(\"c1y\").value;\nlet p1x = +document.getElementById(\"p1x\").value;\nlet p1y = +document.getElementById(\"p1y\").value;\n\nvar canvas;\nvar ctx;\nvar xa = p0x;\nvar ya = p0y;\nvar xca = 110;\nvar yca = 120;\nvar xb = 170;\nvar yb = 100;\nvar xcb = p1x;\nvar ycb = p1y;\nvar WIDTH = 800;\nvar HEIGHT = 300;\nvar dragok = false;\nvar pointA = false;\nvar pointControlA = false;\nvar pointB = false;\nvar pointControlB = false;\n\nfunction rect(x, y, w, h) {\n  ctx.beginPath();\n  ctx.rect(x, y, w, h);\n  ctx.closePath();\n  ctx.fill();\n}\n\nfunction clear() {\n  ctx.clearRect(0, 0, WIDTH, HEIGHT);\n}\n\nfunction init() {\n  canvas = document.getElementById(\"bezier\");\n  ctx = canvas.getContext(\"2d\");\n  canvas.setAttribute(\"width\", WIDTH);\n  canvas.setAttribute(\"height\", HEIGHT);\n  return setInterval(draw, 10);\n}\n\nfunction draw() {\n  clear();\n  ctx.fillStyle = \"#242052\";\n\n  rect(0, 0, WIDTH, HEIGHT);\n  ctx.fillStyle = \"#40e9ff\";\n  rect(xa - 5, ya - 5, 10, 10);\n  rect(xb - 5, yb - 5, 10, 10);\n  ctx.fillStyle = \"#ec008c\";\n  rect(xca - 5, yca - 5, 10, 10);\n  rect(xcb - 5, ycb - 5, 10, 10);\n\n  lerp(xa, ya, xca, yca);\n  lerp(xb, yb, xcb, ycb);\n\n  bezier();\n\n  document.getElementById(\"p0x\").value = +xa;\n  document.getElementById(\"p0y\").value = +ya;\n  document.getElementById(\"c0x\").value = +xca;\n  document.getElementById(\"c0y\").value = +yca;\n  document.getElementById(\"c1x\").value = +xcb;\n  document.getElementById(\"c1y\").value = +ycb;\n  document.getElementById(\"p1x\").value = +xb;\n  document.getElementById(\"p1y\").value = +yb;\n}\n\nfunction lerp(pAx, pAy, pBx, pBy) {\n  ctx.strokeStyle = \"#ec008c\";\n  ctx.beginPath();\n  ctx.moveTo(pAx, pAy);\n  ctx.lineTo(pBx, pBy);\n  ctx.stroke();\n}\n\nfunction bezier() {\n  ctx.strokeStyle = \"#40e9ff\";\n  ctx.beginPath();\n  ctx.moveTo(xa, ya);\n  ctx.bezierCurveTo(xca, yca, xcb, ycb, xb, yb);\n  ctx.stroke();\n}\n\nfunction myMove(e) {\n  if (dragok) {\n    if (pointA) {\n      xa = e.pageX - canvas.offsetLeft;\n      ya = e.pageY - canvas.offsetTop;\n    } else if (pointB) {\n      xb = e.pageX - canvas.offsetLeft;\n      yb = e.pageY - canvas.offsetTop;\n    } else if (pointControlA) {\n      xca = e.pageX - canvas.offsetLeft;\n      yca = e.pageY - canvas.offsetTop;\n    } else if (pointControlB) {\n      xcb = e.pageX - canvas.offsetLeft;\n      ycb = e.pageY - canvas.offsetTop;\n    }\n  }\n}\n\nfunction myDown(e) {\n  /*var debugX = e.pageX - 5 - canvas.offsetLeft;\n    var debugY = e.pageY -5 - canvas.offsetTop;\n    console.log('x '+debugX+' y '+debugY);*/\n  if (\n    e.pageX < xa + 5 + canvas.offsetLeft &&\n    e.pageX > xa - 5 + canvas.offsetLeft &&\n    e.pageY < ya + 5 + canvas.offsetTop &&\n    e.pageY > ya - 5 + canvas.offsetTop\n  ) {\n    xa = e.pageX - canvas.offsetLeft;\n    ya = e.pageY - canvas.offsetTop;\n    dragok = true;\n    pointA = true;\n    canvas.onmousemove = myMove;\n  } else if (\n    e.pageX < xb + 5 + canvas.offsetLeft &&\n    e.pageX > xb - 5 + canvas.offsetLeft &&\n    e.pageY < yb + 5 + canvas.offsetTop &&\n    e.pageY > yb - 5 + canvas.offsetTop\n  ) {\n    xb = e.pageX - canvas.offsetLeft;\n    yb = e.pageY - canvas.offsetTop;\n    dragok = true;\n    pointB = true;\n    canvas.onmousemove = myMove;\n  } else if (\n    e.pageX < xca + 5 + canvas.offsetLeft &&\n    e.pageX > xca - 5 + canvas.offsetLeft &&\n    e.pageY < yca + 5 + canvas.offsetTop &&\n    e.pageY > yca - 5 + canvas.offsetTop\n  ) {\n    xca = e.pageX - canvas.offsetLeft;\n    yca = e.pageY - canvas.offsetTop;\n    dragok = true;\n    pointControlA = true;\n    canvas.onmousemove = myMove;\n  } else if (\n    e.pageX < xcb + 5 + canvas.offsetLeft &&\n    e.pageX > xcb - 5 + canvas.offsetLeft &&\n    e.pageY < ycb + 5 + canvas.offsetTop &&\n    e.pageY > ycb - 5 + canvas.offsetTop\n  ) {\n    xcb = e.pageX - canvas.offsetLeft;\n    ycb = e.pageY - canvas.offsetTop;\n    dragok = true;\n    pointControlB = true;\n    canvas.onmousemove = myMove;\n  }\n}\n\nfunction myUp() {\n  dragok = false;\n  pointA = false;\n  pointControlA = false;\n  pointB = false;\n  pointControlB = false;\n  canvas.onmousemove = null;\n}\n\ninit();\ncanvas.onmousedown = myDown;\ncanvas.onmouseup = myUp;\n\nlet p0ArrX = [];\nlet p0ArrY = [];\nlet PtimeArr = [];\nlet stopPTick = true;\nfunction stopP() {\n  stopPTick = !stopPTick;\n  p0ArrX = [];\n  p0ArrY = [];\n  PtimeArr = [];\n}\n\nfunction refreshP() {\n  let p0x = +document.getElementById(\"p0x\").value;\n  let p0y = +document.getElementById(\"p0y\").value;\n  let c0x = +document.getElementById(\"c0x\").value;\n  let c0y = +document.getElementById(\"c0y\").value;\n  let c1x = +document.getElementById(\"c1x\").value;\n  let c1y = +document.getElementById(\"c1y\").value;\n  let p1x = +document.getElementById(\"p1x\").value;\n  let p1y = +document.getElementById(\"p1y\").value;\n  let timeInt = +document.getElementById(\"timeInt\").value;\n  var p0 = {\n      x: p0x,\n      y: p0y,\n    }, // The first point on curve\n    c0 = {\n      x: c0x,\n      y: c0y,\n    }, // Controller for p0\n    c1 = {\n      x: c1x,\n      y: c1y,\n    }, // Controller for p1\n    p1 = {\n      x: p1x,\n      y: p1y,\n    }; // The last point on curve\n\n  // Apply points to SVG and create the curve and controllers :\n\n  var path = document.getElementById(\"path\"),\n    ctrl1 = document.getElementById(\"ctrl1\"),\n    ctrl2 = document.getElementById(\"ctrl2\"),\n    D =\n      \"M \" +\n      p0.x +\n      \" \" +\n      p0.y +\n      \"C \" +\n      c0.x +\n      \" \" +\n      c0.y +\n      \", \" +\n      c1.x +\n      \" \" +\n      c1.y +\n      \", \" +\n      p1.x +\n      \" \" +\n      p1.y;\n\n  path.setAttribute(\"d\", D);\n  ctrl1.setAttribute(\"d\", \"M\" + p0.x + \",\" + p0.y + \"L\" + c0.x + \",\" + c0.y);\n  ctrl2.setAttribute(\"d\", \"M\" + p1.x + \",\" + p1.y + \"L\" + c1.x + \",\" + c1.y);\n\n  // Lets test the \"Bezier Function\"\n\n  var t = 0,\n    point = document.getElementById(\"point\");\n  let timeCP = 0;\n  let myInterval = setInterval(function () {\n    var p = Bezier(p0, c0, c1, p1, t);\n    point.setAttribute(\"cx\", p.x);\n    point.setAttribute(\"cy\", p.y);\n\n    t += +timeInt;\n    if (t >= 1) {\n      document.getElementById(\"poutputX\").value = p0ArrX;\n      document.getElementById(\"poutputY\").value = p0ArrY;\n      document.getElementById(\"timeP\").value = PtimeArr;\n\n      p0ArrX = [];\n      p0ArrY = [];\n      PtimeArr = [];\n      timeCP = 0;\n      t = 0;\n      clearInterval(myInterval);\n      // p.x = 0;\n      // p.y = 0;\n    } else if (!stopPTick) {\n      clearInterval(myInterval);\n      t = 0;\n      timeCP = 0;\n      stopPTick = !stopPTick;\n    }\n\n    timeCP += timeInt + 0.25;\n    if (p.x < p0x) {\n      p.x * -1;\n    }\n    if (p.y > p0y) {\n      p.y * -1;\n    }\n    p0ArrX.push(((p.x - p0x) / 256).toFixed(3));\n    p0ArrY.push(((p.y - p0y) / 144).toFixed(3));\n\n    PtimeArr.push(timeCP.toFixed(2));\n  }, 50);\n\n  // setTimeout(() => {\n  //     clearInterval(myInterval);\n\n  // }, 5000);\n}\n\n// OK ... Now tring to get \"y\" on cruve based on mouse \"x\" :\n\nvar svg = document.getElementById(\"svg\"),\n  point2 = document.getElementById(\"point2\");\n\n// svg.onmousemove = function (e) {\n\n// var x = (e.pageX - 50) / 2,\n//     y = (e.pageY - 50) / 2;\n// \"-50\" because of \"50px margin\" on the left side\n// and \"/2\" because the svg width is 300 units and 600 px => 300 = 600/2\n\n// Get the x,y by mouse x\n//     var p = YBX(p0, c0, c1, p1, x);\n\n//     point2.setAttribute('cx', p.x);\n//     point2.setAttribute('cy', p.y);\n\n// }\n\n/*\n * Get new x,y on curve by given x\n * @params a,b,c,d {x:x,y:y}\n * @params x\n * @return {{x:new x on cruve ,y: new y on cruve}}\n */\n\nfunction YBX(a, b, c, d, x) {\n  a = a.x;\n  b = b.x;\n  c = c.x;\n  d = d.x;\n\n  // Lets expand this\n  // x = a * (1-t)¬≥ + b * 3 * (1-t)¬≤t + c * 3 * (1-t)t¬≤ + d * t¬≥\n  //------------------------------------------------------------\n  // x = a - 3at + 3at¬≤ - at¬≥\n  //       + 3bt - 6bt¬≤ + 3bt¬≥\n  //             + 3ct¬≤ - 3ct¬≥\n  //                    + dt¬≥\n  //--------------------------------\n  // x = - at¬≥  + 3bt¬≥ - 3ct¬≥ + dt¬≥\n  //     + 3at¬≤ - 6bt¬≤ + 3ct¬≤\n  //     - 3at + 3bt\n  //     + a\n  //--------------------------------\n  // 0 = t¬≥ (-a+3b-3c+d) +  => A\n  //     t¬≤ (3a-6b+3c)   +  => B\n  //     t  (-3a+3b)     +  => c\n  //     a - x              => D\n  //--------------------------------\n\n  var A = d - 3 * c + 3 * b - a,\n    B = 3 * c - 6 * b + 3 * a,\n    C = 3 * b - 3 * a,\n    D = a - x;\n\n  // So we need to solve At¬≥ + Bt¬≤ + Ct + D = 0\n  var t = cubic(A, B, C, D);\n\n  // Replace the t on Bezier function and get x,y\n  var p = Bezier(p0, c0, c1, p1, t);\n\n  return p;\n}\n\n/*\n * Bezier Function\n * Get X,Y by t\n * Refer to https://pomax.github.io/bezierinfo/\n * @params a,b,c,d {x:x,y:y}\n * @params t is between 0-1\n * @return {{x:x on curve ,y:y on curve}}\n */\nfunction Bezier(a, b, c, d, t) {\n  var point = {\n      x: 0,\n      y: 0,\n    },\n    mt = 1 - t,\n    mt2 = mt * mt,\n    mt3 = mt2 * mt;\n\n  //fx(t) = x1 * (1-t)¬≥ + x2 * 3 * (1-t)¬≤t + x3 * 3 * (1-t)t¬≤ + x4 * t¬≥\n  point.x =\n    a.x * mt3 + b.x * 3 * mt2 * t + c.x * 3 * mt * t * t + d.x * t * t * t;\n\n  //fy(t) = y1 * (1-t)¬≥ + y2 * 3 * (1-t)¬≤t + y3 * 3 * (1-t)t¬≤ + y4 * t¬≥\n  point.y =\n    a.y * mt3 + b.y * 3 * mt2 * t + c.y * 3 * mt * t * t + d.y * t * t * t;\n\n  return point;\n}\n\n/*\n * Cubic Equation Calculator\n * refer to http://www.1728.org/cubic.htm\n *\n * ax¬≥ + bx¬≤ + cx + d = 0\n * @params a,b,c,d\n * @return x\n */\n\nfunction cubic(a, b, c, d) {\n  var m, m2, k, n, n2, x, r, rc, theta, sign, dans;\n\n  var f = eval((3 * c) / a - (b * b) / (a * a)) / 3;\n  var g =\n    eval(\n      2 * ((b * b * b) / (a * a * a)) - (9 * b * c) / (a * a) + 27 * (d / a)\n    ) / 27;\n  var h = eval((g * g) / 4 + (f * f * f) / 27);\n\n  if (h > 0) {\n    m = eval(-(g / 2) + Math.sqrt(h));\n    k = m < 0 ? -1 : 1;\n    m2 = eval(Math.pow(m * k, 1 / 3));\n    m2 = m2 * k;\n    n = eval(-(g / 2) - Math.sqrt(h));\n    k = n < 0 ? -1 : 1;\n    n2 = eval(Math.pow(n * k, 1 / 3));\n    n2 = n2 * k;\n    x = eval(m2 + n2 - b / (3 * a));\n  } else {\n    r = eval(Math.sqrt((g * g) / 4 - h));\n    k = r < 0 ? -1 : 1;\n    rc = Math.pow(r * k, 1 / 3) * k;\n    theta = Math.acos(-g / (2 * r));\n    x = eval(2 * (rc * Math.cos(theta / 3)) - b / (3 * a));\n    x = x * 1e14;\n    x = Math.round(x);\n    x = x / 1e14;\n  }\n\n  if (f + g + h == 0) {\n    if (d < 0) {\n      sign = -1;\n    }\n    if (d >= 0) {\n      sign = 1;\n    }\n    if (sign > 0) {\n      dans = Math.pow(d / a, 1 / 3);\n      dans = dans * -1;\n    }\n    if (sign < 0) {\n      d = d * -1;\n      dans = Math.pow(d / a, 1 / 3);\n    }\n    x = dans;\n  }\n\n  return x;\n}\n"
