{"0":"const { useState, useEffect } = React;
const matrix = ((x,y,v) => Array(x).fill(0).map((g) => Array(y).fill(v)));
const CHARS = ["O","X"];
const playerWin = (grid,a,callback) =>{
if(grid[0].every(x=>x===a) 
|| grid[1].every(x=>x===a) 
|| grid[2].every(x=>x===a)
|| (grid[0][0] === a && grid[1][1] === a &&  grid[2][2] === a) 
|| (grid[2][0] === a && grid[1][1] === a &&  grid[0][2] === a)
|| (grid[0][0] === a && grid[1][0] === a &&  grid[2][0] === a)
|| (grid[0][1] === a && grid[1][1] === a &&  grid[2][1] === a)
|| (grid[0][2] === a && grid[1][2] === a &&  grid[2][2] === a)){
 callback();
 return true;
}else return false;
}
const nobodyWins = (turnCount,callback) =>{
  if(turnCount===9){//grid.every(r=>r.every(c=>c!==null))
   callback();
   return true;
  }else return false;
}

const TikTakToe = () => {
  const [loading,setLoading] = useState(true);
  const [turn,setTurn] = useState(null);
  const [grid,setGrid] = useState([]);
  const [message,setMessage] = useState('');
  const [count,setCount] = useState(1);
  useEffect(()=>{
  setTurn(0);
  setCount(1);
  setGrid(matrix(3,3,null));
  setLoading(false);
  },[loading])

  const playTurn = (i,j,el)=>{
   if(grid[i][j]===null){
   grid[i][j] = turn;
   el.textContent = CHARS[turn]
   setGrid(grid);
   if(playerWin(grid,turn,()=>setMessage(`player ${CHARS[turn]} wins`)) 
   || nobodyWins(count,()=>setMessage('Nobody wins'))){
    setTimeout(()=>{
    setLoading(true);
    setLoading(false);
    setMessage('');
    },1500);
   }
   setTurn(1-turn);
   setCount(count+1);  
    }

  }
  const cellSize = 100;
  return (
    !loading && <div style={{textAlign:'center',
        grid:"auto", maxWidth:((cellSize+cellSize*0.2)*3)+"px"}} >
      {matrix(3,3,0).map((col,i)=> col.map((row,j) => <button onClick={(e)=>playTurn(i,j,e.target)} 
             style={{width:cellSize+'px',height:cellSize+'px', margin:0.3+'%', 
             border:'2px solid black',fontWeight:900, fontSize:'25px'}}
      	   key={i+j}> {"\u00a0"} </button> 
        ))}
       <p>{message}</p>
    </div>
  );
}

ReactDOM.render(
  <React.StrictMode>
    <TikTakToe />
  </React.StrictMode>,
  document.getElementById('root'),
);

