{"0":"const { useState, useEffect, createContext, useContext } = React;
const scoresContext = createContext([0, 0]);
const matrix = (x, y, v) =>
  Array(x)
    .fill(0)
    .map((g) => Array(y).fill(v));
const CHARS = ["O", "X"];
const COLORS = ["#1589FF", "#FF0043"];
const victoryFields = [
  { x: [0, 0, 0], y: [0, 1, 2] },
  { x: [1, 1, 1], y: [0, 1, 2] },
  { x: [2, 2, 2], y: [0, 1, 2] },

  { x: [0, 1, 2], y: [0, 1, 2] },
  { x: [2, 1, 0], y: [0, 1, 2] },

  { x: [0, 1, 2], y: [0, 0, 0] },
  { x: [0, 1, 2], y: [1, 1, 1] },
  { x: [0, 1, 2], y: [2, 2, 2] },
];

const playerWin = (grid, player, callback) => {
  const cells = victoryFields.find((curr) =>
    [
      grid[curr.x[0]][curr.y[0]],
      grid[curr.x[1]][curr.y[1]],
      grid[curr.x[2]][curr.y[2]],
    ].every((g) => g === player)
  );
  if (cells) {
    callback(cells);
    return true;
  } else return false;
};
const nobodyWins = (turnCount, callback) => {
  if (turnCount === 9) {
    return true;
  } else return false;
};
const colorWinningCells = (grid, cells, color) => {
  const winnerGrid = [...grid];
  winnerGrid[cells.x[0]][cells.y[0]] = color;
  winnerGrid[cells.x[1]][cells.y[1]] = color;
  winnerGrid[cells.x[2]][cells.y[2]] = color;
  return winnerGrid;
};
const TicTacToe = ({ autoReset }) => {
  const [winnerCells, setWinnerCells] = useState(null);
  const scores = useContext(scoresContext);
  const setScore = (player, score) => (scores[player] = score);
  const [loading, setLoading] = useState(true);
  const [turn, setTurn] = useState(null);
  const [grid, setGrid] = useState([]);
  const [count, setCount] = useState(1);
  const [block, blockGame] = useState(true);
  useEffect(() => {
    setWinnerCells(null);
    setTurn(0);
    setCount(1);
    setGrid(matrix(3, 3, null));
    setLoading(false);
    blockGame(false);
  }, [loading]);

  const playTurn = (i, j, el) => {
    if (!block && grid[i][j] === null) {
      grid[i][j] = turn;
      el.textContent = CHARS[turn];
      el.style.color = COLORS[turn];
      setGrid(grid);
      if (
        playerWin(grid, turn, (cells) => {
          setScore(turn, scores[turn] + 1);
          setWinnerCells(colorWinningCells(grid, cells, COLORS[turn]));
        }) ||
        nobodyWins(count)
      ) {
        blockGame(true);
        setTimeout(() => {
          if (autoReset) {
            setLoading(true);
          }
        }, 2000);
      }
      setTurn(1 - turn);
      setCount(count + 1);
    }
  };
  const cellSize = 200;
  return (
    !loading && (
      <div
        style={{
          textAlign: "center",
          grid: "auto",
          maxWidth: (cellSize + cellSize * 0.2) * 3 + "px",
        }}
      >
        {matrix(3, 3, 0).map((row, i) =>
          row.map((col, j) => (
            <button
              onClick={(e) => playTurn(i, j, e.target)}
              style={{
                filter: `grayScale(${
                  block && !autoReset
                    ? typeof winnerCells[i][j] === "string"
                      ? 0
                      : 90
                    : 0
                }%)`,
                width: cellSize + "px",
                height: cellSize + "px",
                margin: 0.3 + "%",
                background: "transparent",
                border:
                  "2px solid " +
                  (winnerCells !== null ? winnerCells[i][j] : "#dddddd"),
                fontWeight: 900,
                fontSize: "55px",
                cursor: "pointer",
              }}
              key={i + j}
            >
              {"\u00a0"}
            </button>
          ))
        )}
        <div
          style={{
            textAlign: "center",
            color: "#dddddd",
            fontSize: "30px",
            fontFamily: "Consolas",
            fontWeight: 900,
          }}
        >
          <span style={{ color: COLORS[0] }}>{scores[0]}</span>
          <span style={{ margin: "12%" }}>{`Turn:${count}`}</span>
          <span style={{ color: COLORS[1] }}>{scores[1]}</span>
        </div>
      </div>
    )
  );
};

ReactDOM.render(
  <React.StrictMode>
    <TicTacToe autoReset={true} />
  </React.StrictMode>,
  document.getElementById("root")
);
