{"0":"const { useState, useEffect } = React;
console.clear();
const matrix = ((x,y,v) => Array(x).fill(0).map((g) => Array(y).fill(v)));
const CHARS = ["O","X"];
const playerWin = (grid,a) => grid[0].every(x=>x===a) 
|| grid[1].every(x=>x===a) 
|| grid[2].every(x=>x===a)
|| (grid[0][0] === a && grid[1][1] === a &&  grid[2][2] === a) 
|| (grid[2][0] === a && grid[1][1] === a &&  grid[0][2] === a)
|| (grid[0][0] === a && grid[1][0] === a &&  grid[2][0] === a)
|| (grid[0][1] === a && grid[1][1] === a &&  grid[2][1] === a)
|| (grid[0][2] === a && grid[1][2] === a &&  grid[2][2] === a)

const TikTakToe = () => {
  const [loading,setLoading] = useState(true);
  const [round,setRound] = useState(0);
  const [turn,setTurn] = useState(null);
  const [grid,setGrid] = useState([]);

  useEffect(()=>{
  setTurn(0);
  setGrid(matrix(3,3,null));
  setLoading(false)
  },[round])

  const playTurn = (i,j,el)=>{
   if(grid[i][j]===null){
   grid[i][j] = turn;
   el.textContent = CHARS[turn]
   setGrid(grid);
   if(playerWin(grid,turn)){
    alert(`player ${CHARS[turn]} wins`);
    setRound(round+1)
    setLoading(true)
   }
   setTurn(1-turn);
    }
  }

  return (
    !loading && <div style={{textAlign:'center',marginTop:'25%'}}>
      {matrix(3,5,0).map((col,i)=> col.map((row,j) => (j) % 4 === 0 ?  <div></div> : 
             <button onClick={(e)=>playTurn(i,j-1,e.target)} 
             style={{width:'130px',height:'130px', margin:'0.2%', 
             border:'2px solid black',fontWeight:900, fontSize:'25px'}}
      	   key={i+j}> {"\u00a0"} </button> 
        ))}
    </div>
  );
}

ReactDOM.render(
  <React.StrictMode>
    <TikTakToe />
  </React.StrictMode>,
  document.getElementById('root'),
);

