// loop solution
const removeElements = (head, targetVal) => {
  while (head && head.val === targetVal) {
    head = head.next;
  }

  let currentNode = head;

  while (currentNode && currentNode.next) {
    if (currentNode.next.val === targetVal) {
      currentNode.next = currentNode.next.next;
    } else {
      currentNode = currentNode.next;
    }
  }

  return head;
};

// recursion solution
const removeElements = (head, targetVal) => {
  if (head === null) {
    return head;
  } else {
    head.next = removeElements(head.next, targetVal);

    if (head.val === targetVal) {
      return head.next;
    } else {
      return head;
    }
  }
};
